// InputTodo.js
import { useState } from "react";

const InputTodo = ({ todo, setTodo }) => {
  const [value, setValue] = useState("");

  const saveTodo = () => {
    if (value.trim() !== "") {
      setTodo([
        ...todo,
        {
          id: Math.random(),
          title: value.trim(),
          status: true,
        },
      ]);
      setValue("");
    }
  };


  const onDelete =(id)=>{
const newTask = [...todo].filter(e=> e.id != id)
setTodo(newTask)
  }


  const onSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <input
          value={value}
          onChange={({ target }) => setValue(target.value)}
        />
        <button type="submit" onClick={saveTodo}>
          Сохранить
        </button>
      </form>
      {todo.map((e) => (
        <>
          {" "}
          <div key={e.id}>{e.title}</div>
          <button type="button" onClick={()=>onDelete(e.id)}>удалить</button>
        </>
      ))}
    </div>
  );
};

export default InputTodo;
