import React, { useState } from "react";

const TaskTodo = ({ todo, deleteTask, updateTask }) => {
  const [edit, setEdit] = useState(null);
  const [value, setValue] = useState("");

  const editTodo = (id, title) => {
    setEdit(id);
    setValue(title);
  };

  const handleSave = (id) => {
    updateTask(id, value);
    setEdit(null); // Убираем режим редактирования после сохранения
  };

  return (
    <div>

      {todo.map((e) => (
        <div key={e.id}>
          {edit === e.id ? (
            <div>
              <input
                value={value}
                onChange={({ target }) => setValue(target.value)}
              />
              <button type="button" onClick={() => handleSave(e.id)}>
                Сохранить
              </button>
            </div>
          ) : (
            <div> {e.title}</div>
          )}

          <div>
            <button type="button" onClick={() => deleteTask(e.id)}>
              удалить
            </button>
            <button type="button" onClick={() => editTodo(e.id, e.title)}>
              редактировать
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default TaskTodo;
