const TaskTodo = ({ todo, deleteTask, flag, toggle }) => {
  return (
    <div>
      {todo.map((e) => (
        <div key={e.id}>
          {flag ? <input></input> : <div> {e.title}</div>}
          <button type="button" onClick={() => deleteTask(e.id)}>
            удалить
          </button>
          <button type="button" onClick={toggle}>
            изменить
          </button>
        </div>
      ))}
    </div>
  );
};

export default TaskTodo;
