import React, { useState } from "react";

const TaskTodo = ({ todo, deleteTask }) => {
  const [edit, setEdit] = useState(false);

  const editTodo = (id) => {
    setEdit(true);
  };

  return (
    <div>
      {todo.map((e) => (
        <div key={e.id}>
          {edit===e.id ? (
            <div>
              <input />
              <button>сохранить</button>
            </div>
          ) : (
            <div> {e.title}</div>
          )}

          {
            edit===e.id ? <div><button>сохранить</button></div> : <div>  <button type="button" onClick={() => deleteTask(e.id)}>
            удалить
          </button>
          <button type="button" onClick={() => editTodo(e.id)}>
            редактировать
          </button></div>
          }
         
        
        </div>
      ))}
    </div>
  );
};

export default TaskTodo;
