import React, { useState } from "react";

const TaskTodo = ({ todo, deleteTask }) => {
  const [edit, setEdit] = useState(null);
  const [value, setValue] = useState("");

  const editTodo = (id, title) => {
    setEdit(id);
    setValue(title);
  };


 
const updateTask = (id, newValue) => {
  fetch(`http://localhost:3007/products/${id}`, {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json;charset=utf-8' },
    body: JSON.stringify({
      value: newValue
    })
  })
    .then((rawResponse) => rawResponse.json())
    .then((response) => {
      const updatedTasks = task.map((t) =>
        t.id === id ? { ...t, value: newValue } : t
      );
      setTask(updatedTasks);
    });
};

  return (
    <div>
      {todo.map((e) => (
        <div key={e.id}>
          {edit == e.id ? (
            <div>
              <input
                value={value}
                onChange={({ target }) => setValue(target.value)}
              />
              <button type="button" onClick={()=>updateTask(e.id, e.title)}>
                Сохранить
              </button>
            </div>
          ) : (
            <div> {e.title}</div>
          )}

          <div>
            <button type="button" onClick={() => deleteTask(e.id)}>
              удалить
            </button>
            <button type="button" onClick={() => editTodo(e.id, e.title)}>
              редактировать
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default TaskTodo;
