// InputTodo.js
import { useState } from "react";

const InputTodo = ({ todo, setTodo }) => {
  const [value, setValue] = useState("");


  const addTask = () =>{
    fetch('http://localhost:3008/products',{
    method: 'POST',
    headers:{'Content-Type': 'application/json;charset=utf-8'},
    body: JSON.stringify({
      title:value,
      status:false
    })
  }).then((rawResponse)=>rawResponse.json()).then((response)=>{
    let newTask = [response, ...todo];
    setTodo(newTask);
    setValue('')
  })};


  const onDelete = (id) => {
    const newTask = [...todo].filter((e) => e.id !==  id);
    setTodo(newTask);
  };

  const deleteTask = (id) => {
    fetch(`http://localhost:3007/products/${id}`, {
      method: 'DELETE'})
  }

  const onSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <input
          value={value}
          onChange={({ target }) => setValue(target.value)}
        />
        <button type="submit" onClick={addTask}>
          Сохранить
        </button>
      </form>
      {todo.map((e) => (
        <>
          {" "}
          <div key={e.id}>{e.title}</div>
          <button type="button" onClick={() => onDelete(e.id)}>
            удалить
          </button>
        </>
      ))}
    </div>
  );
};

export default InputTodo;
