// InputTodo.js
import { useState, useEffect } from "react";

const InputTodo = ({ todo, setTodo }) => {
  const [value, setValue] = useState("");
  const [flag, setFlag] = useState(false);

  useEffect(() => {
    fetch("http://localhost:3008/products")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Ошибка загрузки задач");
        }
        return response.json();
      })
      .then((data) => {
        setTodo(data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const addTask = () => {
    fetch("http://localhost:3008/products", {
      method: "POST",
      headers: { "Content-Type": "application/json;charset=utf-8" },
      body: JSON.stringify({
        title: value,
        status: false,
      }),
    })
      .then((rawResponse) => rawResponse.json())
      .then((response) => {
        let newTask = [response, ...todo];
        setTodo(newTask);
        setValue("");
      });
  };

  /*  const onDelete = (id) => {
    const newTask = [...todo].filter((e) => e.id !== id);
    setTodo(newTask);
  };*/

  const deleteTask = (id) => {
    fetch(`http://localhost:3008/products/${id}`, {
      method: "DELETE",
    });
    const newTask = [...todo].filter((e) => e.id !== id);
    setTodo(newTask);
  };

  const onSubmit = (e) => {
    e.preventDefault();
  };

  const toggle = (id) => {
    setFlag(!flag);
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <input
          value={value}
          onChange={({ target }) => setValue(target.value)}
        />
        <button type="submit" onClick={addTask}>
          Сохранить
        </button>
      </form>
      {todo.map((e) =>
        
          <div key={e.id}>
            {e.title}
            <button type="button" onClick={() => deleteTask(e.id)}>
              удалить
            </button>
         
          </div>
        
      )}
    </div>
  );
};

export default InputTodo;
