import { useState, useEffect } from "react";
import TaskTodo from "../TaskTodo/TaskTodo";

const InputTodo = () => {
  const [value, setValue] = useState("");
  const [todo, setTodo] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetch("http://localhost:3009/products")
      .then((response) => response.json())
      .then((data) => {
        setTodo(data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const addTask = () => {
    fetch("http://localhost:3009/products", {
      method: "POST",
      headers: { "Content-Type": "application/json;charset=utf-8" },
      body: JSON.stringify({
        title: value,
        status: false,
      }),
    })
      .then((rawResponse) => rawResponse.json())
      .then((response) => {
        let newTask = [response, ...todo];
        setTodo(newTask);
        setValue("");
      });
  };

  const deleteTask = (id) => {
    fetch(`http://localhost:3009/products/${id}`, {
      method: "DELETE",
    });
    const newTask = [...todo].filter((e) => e.id !== id);
    setTodo(newTask);
  };

  const onSubmit = (e) => {
    e.preventDefault();
  };

  const updateTask = (id, newValue) => {
    fetch(`http://localhost:3009/products/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        title: newValue,
        status: false,
      }),
    }).then((response) => {
      const newTask = todo.map((task) =>
        task.id === id ? { ...task, title: newValue } : task
      );
      setTodo(newTask);
    });
  };

  const filter = () => {
    const filteredTodo = todo.filter((task) =>
      task.title.toLowerCase().includes(search.toLowerCase())
    );
    setTodo(filteredTodo)
  };

  const sortTodo =()=>{
    const sorted=todo.title.sort();
    setTodo(sorted)
  }

  return (
    <div>
      <form onSubmit={onSubmit}>
        <input
          value={value}
          onChange={({ target }) => setValue(target.value)}
        />
        <button type="submit" onClick={addTask}>
          Сохранить
        </button>
      </form>

      <div>
        <input
          value={search}
          onChange={({ target }) => setSearch(target.value)}
        />
        <button type='button' onClick={filter}>filter</button>
      </div>

      <TaskTodo todo={todo} deleteTask={deleteTask} updateTask={updateTask} />
    </div>
  );
};

export default InputTodo;
